const input = items[0].json;
const body = input.body || input.data || input;
const elements = body.elements || [];

const out = elements.map(e => {
  const message = (e.message && (e.message.text || e.message)) || '';
  const actor = e.actor || null;
  const created = e.created && e.created.time ? e.created.time : null;

  // Extract username/author info
  let username = "";
  let authorName = "";

  if (e['actor~']) {
    authorName = `${e['actor~'].firstName || ''} ${e['actor~'].lastName || ''}`.trim();
  }
  if (e.username) {
    username = e.username;
  }
  if (e.author && e.author.name) {
    authorName = e.author.name;
    username = e.author.username || username;
  }

  return {
    json: {
      commentId: e.id || null,
      text: typeof message === 'string' ? message : '',
      actorUrn: actor,
      created: created,
      username: username,
      authorName: authorName,
      raw: e
    }
  };
});

return out;

