// Parse clean AI response (no "output" wrapper expected)
const aiResponse = items[0].json;

console.log("DEBUG - Raw AI Response:", JSON.stringify(aiResponse, null, 2));

// AI should now return clean JSON like:
// {"commentId":"123","username":"john","interested":true,"reason":"...","confidence":0.95}

let classification = aiResponse;

// If somehow still wrapped, handle it
if (aiResponse.output) {
  console.log("WARNING: Still getting 'output' wrapper");
  try {
    classification = JSON.parse(aiResponse.output.replace(/\\"/g, '"'));
  } catch(e) {
    classification = { error: "Parse failed", raw: aiResponse };
  }
}

// Get original comment
const originalComment = $input.all()[0].json;

const result = {
  commentId: classification.commentId || originalComment.commentId,
  username: classification.username || originalComment.username || "unknown",
  text: originalComment.text || "",
  actorUrn: originalComment.actorUrn || "",
  created: originalComment.created || null,
  interested: classification.interested || false,
  reason: classification.reason || "No reason provided",
  confidence: classification.confidence || 0.0
};

return [{ json: result }];

// Debug: Check what's reaching IF node
console.log("IF Node Input:", JSON.stringify(items[0].json, null, 2));
console.log("interested value:", items[0].json.interested);
console.log("interested type:", typeof items[0].json.interested);

return items;
